import React from 'react';
import { 
  Box, 
  TextField, 
  FormControl, 
  InputLabel, 
  Select, 
  MenuItem, 
  Slider, 
  Typography 
} from '@mui/material';

const FilterPanel = ({
  searchTerm,
  setSearchTerm,
  selectedSystem,
  setSelectedSystem,
  politicalSystems,
  opacityValue,
  setOpacityValue,
  minGdpFilter,
  maxGdpFilter,
  setMinGdpFilter,
  setMaxGdpFilter,
  maxGdp,
  isMobile
}) => {
  return (
    <Box>
      {/* Title and Search/Filter Row */}
      <Box sx={{ 
        mb: 2, 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: isMobile ? 'flex-start' : 'center', 
        flexDirection: isMobile ? 'column' : 'row',
        flexWrap: 'wrap', 
        gap: 2 
      }}>
        <Typography variant="h6" sx={{ mb: isMobile ? 1 : 0 }}>
          Interactive Country Data Visualization
        </Typography>
        
        <Box sx={{ 
          display: 'flex', 
          gap: 2, 
          flexDirection: isMobile ? 'column' : 'row',
          flexWrap: 'wrap',
          width: isMobile ? '100%' : 'auto'
        }}>
          <TextField
            label="Search Countries"
            variant="outlined"
            size="small"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            sx={{ minWidth: isMobile ? '100%' : 150 }}
            placeholder="e.g., United States, Germany..."
          />
          
          <FormControl sx={{ minWidth: isMobile ? '100%' : 200 }} size="small">
            <InputLabel id="political-system-label">Political System</InputLabel>
            <Select
              labelId="political-system-label"
              value={selectedSystem}
              label="Political System"
              onChange={(e) => setSelectedSystem(e.target.value)}
            >
              {politicalSystems.map(system => (
                <MenuItem key={system} value={system}>{system}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>
      </Box>
      
      {/* GDP Range Slider */}
      <Box sx={{ mb: 2 }}>
        <Typography gutterBottom>
          GDP Per Capita Range (USD): ${minGdpFilter.toLocaleString()} - ${(maxGdpFilter === Infinity ? maxGdp : maxGdpFilter).toLocaleString()}
        </Typography>
        <Slider
          value={[
            minGdpFilter, 
            maxGdpFilter === Infinity ? maxGdp : maxGdpFilter
          ]}
          min={0}
          max={maxGdp}
          step={5000}
          onChange={(e, newValue) => {
            setMinGdpFilter(newValue[0]);
            setMaxGdpFilter(newValue[1]);
          }}
          valueLabelDisplay="auto"
          valueLabelFormat={(value) => `$${value.toLocaleString()}`}
          sx={{ 
            '& .MuiSlider-thumb': {
              width: isMobile ? 24 : 20,
              height: isMobile ? 24 : 20,
            }
          }}
        />
      </Box>
      
      {/* Opacity Slider */}
      <Box sx={{ mb: 2 }}>
        <Typography gutterBottom>
          Dot Opacity: {Math.round(opacityValue * 100)}%
        </Typography>
        <Slider
          value={opacityValue}
          min={0.1}
          max={1}
          step={0.1}
          onChange={(e, newValue) => setOpacityValue(newValue)}
          valueLabelDisplay="auto"
          valueLabelFormat={(value) => `${Math.round(value * 100)}%`}
          sx={{ 
            '& .MuiSlider-thumb': {
              width: isMobile ? 24 : 20,
              height: isMobile ? 24 : 20,
            }
          }}
        />
      </Box>
    </Box>
  );
};

export default FilterPanel;
