import React from 'react';
import { Box, CircularProgress, Typography, LinearProgress } from '@mui/material';

const LoadingSpinner = ({ message = 'Loading data...', showProgress = false, progress = 0 }) => {
  return (
    <Box 
      sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        alignItems: 'center', 
        justifyContent: 'center',
        minHeight: '300px',
        p: 4
      }}
    >
      <CircularProgress size={60} thickness={4} sx={{ mb: 2 }} />
      
      <Typography variant="h6" color="text.secondary" gutterBottom>
        {message}
      </Typography>
      
      <Typography variant="body2" color="text.secondary" sx={{ mb: 2, textAlign: 'center' }}>
        Please wait while we fetch and process the country data...
      </Typography>
      
      {showProgress && (
        <Box sx={{ width: '100%', maxWidth: 400 }}>
          <LinearProgress variant="determinate" value={progress} />
          <Typography variant="caption" color="text.secondary" sx={{ mt: 1 }}>
            {Math.round(progress)}% complete
          </Typography>
        </Box>
      )}
      
      <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
        <Box sx={{ width: 8, height: 8, bgcolor: 'primary.main', borderRadius: '50%', animation: 'pulse 1.5s ease-in-out infinite' }} />
        <Box sx={{ width: 8, height: 8, bgcolor: 'primary.main', borderRadius: '50%', animation: 'pulse 1.5s ease-in-out 0.3s infinite' }} />
        <Box sx={{ width: 8, height: 8, bgcolor: 'primary.main', borderRadius: '50%', animation: 'pulse 1.5s ease-in-out 0.6s infinite' }} />
      </Box>
      
      <style jsx>{`
        @keyframes pulse {
          0%, 70%, 100% {
            opacity: 0.3;
            transform: scale(0.8);
          }
          35% {
            opacity: 1;
            transform: scale(1);
          }
        }
      `}</style>
    </Box>
  );
};

export default LoadingSpinner;
